import React, { Component } from "react";
import Helmet from "react-helmet";

const background_image = {
  background_image: '#fff'
};

export default class Head extends Component {
  render() {
    let body;

    if (this.props.aqi <= 50) {
      body = (
        <body style={background_image} />
      );
    } else if (this.props.aqi >= 51 && this.props.aqi <= 100) {
      body = (
        <body style={background_image} />
      );
    } else if (this.props.aqi >= 101 && this.props.aqi <= 150) {
      body = (
        <body style={{background_image}} />
      );
    } else if (this.props.aqi > 150) {
      body = (
        <body style="background-image: linear-gradient(to right top, #c60000, #d40000, #e20000, #f00000, #ff0000); color: white" />
      );
    }

    return (
      <div>
        <Helmet>{body}</Helmet>
      </div>
    );
  }
}
